(window.webpackJsonp=window.webpackJsonp||[]).push([[8],{393:function(t,s,p){t.exports=p.p+"assets/img/a-1.e023e5cc.png"},394:function(t,s,p){t.exports=p.p+"assets/img/1.a86f2b03.png"},395:function(t,s,p){t.exports=p.p+"assets/img/2.db1d76ca.png"},396:function(t,s,p){t.exports=p.p+"assets/img/3.e7389d09.png"},397:function(t,s,p){t.exports=p.p+"assets/img/a-2.87b858ef.png"},398:function(t,s,p){t.exports=p.p+"assets/img/4.001adbfc.png"},399:function(t,s,p){t.exports=p.p+"assets/img/5.3c6346de.png"},400:function(t,s,p){t.exports=p.p+"assets/img/6.8e8b3e82.png"},401:function(t,s,p){t.exports=p.p+"assets/img/7.f4641391.png"},434:function(t,s,p){"use strict";p.r(s);var a=p(2),n=Object(a.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"人民币的闷杀局"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#人民币的闷杀局"}},[t._v("#")]),t._v(" 人民币的闷杀局")]),t._v(" "),s("p",[t._v("我想绝大部分中国人都喜欢人民币，且会把人民币与金钱画上等号，在我看来这实在是一种很粗浅的认知，我认为人民币的背后实际上是一个局，我称之为对中国人的闷杀局。")]),t._v(" "),s("p",[t._v("首先我抛出自己的观点，就目前中国的政策来看，人民币不存在大幅贬值的基础，但是未来有一天人民币会消失。下面我来解释一下闷杀局的原理。")]),t._v(" "),s("p",[t._v("我把中国的货币分为4种，1. 离岸人民币 2. 港币 3. 镰刀型人民币 4. 韭菜型人民币，其中普通中国人持有的韭菜型人民币是受到外汇管制不可以进行自由兑换的。")]),t._v(" "),s("p",[s("img",{attrs:{src:p(393),alt:"a-1"}})]),t._v(" "),s("p",[t._v("我们来看看镰刀型人民币背后的运作逻辑。中国近年来成立了很多个自由贸易区，以上海自贸区为例，在上海自贸区的政策中就明确指出部分在此设立的企业可以自由兑换汇，很明显这是权贵给外资的特权，以此名正言顺地勾结，所以他们这些人手里的人民币我就称之为镰刀型人民币。")]),t._v(" "),s("p",[s("img",{attrs:{src:p(394),alt:"1"}})]),t._v(" "),s("p",[s("img",{attrs:{src:p(395),alt:"2"}})]),t._v(" "),s("p",[t._v("举个镰刀型人民币的例子，美国总统拜登上台那段时间被曝出他的儿子亨特在渤海华美身居要职，这个渤海华美就是一家注册在上海自贸区的股权投资基金管理公司。")]),t._v(" "),s("p",[s("img",{attrs:{src:p(396),alt:"3"}})]),t._v(" "),s("p",[t._v("那么闷杀局具体怎么个闷杀法呢，请看下图。")]),t._v(" "),s("p",[s("img",{attrs:{src:p(397),alt:"a-2"}})]),t._v(" "),s("p",[t._v("中国财富金字塔的塔尖是外资和权贵持有的镰刀币，第二层是一些境外投资者持有的离岸人民币和港币，这两类目前都是可以自由兑换的，而金字塔的底层占了整体的大部分，是由普通中国人持有的不可以自由兑换的韭菜币组成。由于不可以自由兑换，韭菜币就只能在中国大陆内部循环，在机构和政府的操纵下用来推高不同资产的价格，其中最典型的就是楼市和股市。首先是外资和权贵入场，资产价格从A点开始上涨，然后是相对幸运的早期韭菜入场，当资产价格上升之后做局人开始大肆鼓吹中国市场，韭菜们信以为真，随后大量为了获利的韭菜币入场，泡沫泛起，进一步推高资产价格，当价格被炒到最高点的时候，镰刀开始收割，泡沫开始破灭，之后镰刀币自由地兑换成外汇然后离场，资产价格跌到C点，完成一轮周期。")]),t._v(" "),s("p",[t._v("这种闷杀局的关键点就在于政府设计出来的韭菜型人民币，在这个过程中由于占了整体大部分的韭菜币无法自由兑换，所以并不会造成大量资本外逃，因此人民币汇率可以保持稳定。不管是中国的楼市还是股市，都是通过韭菜币不断推高的，然而韭菜币无法随意兑换离场，镰刀币却可以，不仅如此，由于人民币汇率的稳定，镰刀币还能以最高的价格把钱兑换成外汇，转移到美国、瑞士去，成为他们真正想要的资产。")]),t._v(" "),s("p",[t._v("人民币闷杀局的设计是非常巧妙的，因为这打造了一个多赢的格局。首先是权贵收割了大量财富，然后将这些财富转移到境外，逍遥快活；其次外资也赚得盆满钵满，豪车别墅，荒淫无度；最后是韭菜，他们被长期洗脑，总喜欢站在权贵的立场上看问题，认为国富民强，自己把自己感动得热泪盈眶，殊不知，他们仅仅是权贵制造财富的工具人而已，整个中国的财富，都是锚定在韭菜身上的。没有韭菜的血汗，绝不会有权贵的野蛮。总结来说，中国的镰刀是踩在韭菜头上收割的，但韭菜对此一直麻木不仁。")]),t._v(" "),s("p",[t._v("看到这里，我想我已经把闷杀局的原理讲得比较清楚了，下面提供几张图给大家，是中国从2020年到2022年7月的结售汇数据(关注股市的可以重点看资本与金融项目这一行)和2022年以来中国的外汇储备变化情况，各位有兴趣可以自己分析一下，截止到本文发出的时候，程序已经走到A、B、C中的哪个阶段了。")]),t._v(" "),s("p",[s("img",{attrs:{src:p(398),alt:"4"}})]),t._v(" "),s("p",[s("img",{attrs:{src:p(399),alt:"5"}})]),t._v(" "),s("p",[s("img",{attrs:{src:p(400),alt:"6"}})]),t._v(" "),s("p",[s("img",{attrs:{src:p(401),alt:"7"}})]),t._v(" "),s("p",[t._v("多说一嘴，我想能看到这篇文章的人当中还是会有很多小粉红，他们看到这里的时候肯定忍不住大骂(或者小声骂)我是“汉奸”，但我想说的是，我分析事情只看数据和事实，从来都不希望参杂感情进去，也在不断训练自己摆脱情绪化分析。本文不指向任何一个具体的中国人，仅仅从宏观上进行分析，如果本文的内容冒犯到了你，请立刻关闭它，谢谢。")]),t._v(" "),s("p",[t._v("最后送大家一句话吧，“天雨大，不润无根之草；道法宽，只渡有缘之人。”，祝各位好运。")])])}),[],!1,null,null,null);s.default=n.exports}}]);